    /*
     * Listing of tb_pasien
     */
    function index()
    {
        $data['tb_pasien'] = $this->Tb_pasien_model->get_all_tb_pasien();
        
        $data['_view'] = 'tb_pasien/index';
        $this->load->view('layouts/main',$data);
